import React, { useState, useEffect, useRef } from 'react';

import useInterval from '../../custom-hooks';

import SliderImage from '../slider-img';

import './index-page.css';

const IndexPage = () => {

    const [x, setX] = useState(0);

    const savedCallback = useRef();
    
    useEffect(()=>{

    })
    
    setTimeout(() => {
        
    }, 3000);

    // useEffect(() => {
    //     savedCallback.current = setInterval(goRight, 1000);
    // }, [savedCallback])

    let sliderArr = [
        <SliderImage ind="1" />,
        <SliderImage ind="2" />,
        <SliderImage ind="3" />
    ];

    const goLeft = () => {
        x === 0 ? setX(-100 * (sliderArr.length - 1)) : setX(x => x + 100);
    }

    const goRight = () => {
        setX(x => x === -100 * (sliderArr.length - 1) ? 0 : x - 100)
    }
    // const hooks = useInterval(goRight, 1000)
    
    return (
        <div className="slider">
            {sliderArr.map((item, ind) => {
                return (
                    <div key={ind} className="slide" style={{ transform: `translateX(${x}%)` }}>
                        {item}
                    </div>
                )
            })}
            <button
                className="slider__button slider__button--left"
                onClick={goLeft}>
                <i class="fa fa-chevron-left arrow" aria-hidden="true"></i>
            </button>
            <button
                className="slider__button slider__button--right"
                onClick={goRight}>
                <i class="fa fa-chevron-right arrow" aria-hidden="true"></i>
            </button>
        </div >
        // <div className="image-carousel">
        //     <div className="inner">
        //         <img src="http://localhost:8000/static/store_app/1.jpg" alt="" />
        //         <img src="http://localhost:8000/static/store_app/2.jpg" alt="" />
        //         <img src="http://localhost:8000/static/store_app/3.jpg" alt="" />
        //     </div>
        //     <div className="bubbles">
        //         <span className="bubble bubble__active"></span>
        //         <span className="bubble"></span>
        //         <span className="bubble"></span>
        //     </div>
        //     <div className="prev"></div>
        //     <div className="next"></div>
        // </div>
    )
}

export default IndexPage;
