import {
    SET_LOGIN_MODAL_WINDOW,
    GET_USER_LOADING,
    GET_USER_LOADED,
    GET_USER_FAILURE
} from '../redux/action-types';
import axios from 'axios';

const link = 'http://127.0.0.1:8000/api/';

export const setLoginModalWindow = data => ({
    type: SET_LOGIN_MODAL_WINDOW, payload: data
})

export const getUser = (data) => (dispatch, getState) => {
    dispatch({ type: GET_USER_LOADING })

    const token = getState().auth.token;

    if (!token) {
        return dispatch({ type: GET_USER_FAILURE })
    }

    axios.post(`${link}accounts/user/`, {
        headers: {
            Authorization: 'Token abbf6c03580dacdc3ee72015cba3c97b47658554a3d564bed994f7486572dd09',
            
        }
    })
}
