import {
    GET_USER_LOADING,
    GET_USER_LOADED,
    GET_USER_FAILURE
} from "../action-types";

const initialState = {
    token: localStorage.getItem('token'),
    isAuthenticated: false,
    isLoading: false,
    error: null,
    user: null
}

const modals = (state = initialState, action) => {
    switch (action.type) {
        case SET_LOGIN_MODAL_WINDOW: {
            return {
                ...state,
                loginPopup: action.payload
            }
        }
        default: {
            return state;
        }
    }
};

export default modals;
