import React, { useState } from 'react';

import SliderImage from '../slider-img';

import './index-page.css';

const IndexPage = () => {

    const [x, setX] = useState(0);
    let sliderArr = [
        <SliderImage ind="1" />,
        <SliderImage ind="2" />,
        <SliderImage ind="3" />
    ];
    console.log(x)

    const goLeft = () => {
        x === 0 ? setX(-100 * (sliderArr.length - 1)) : setX(x + 100);
    }

    const goRight = () => {
        x === -100 * (sliderArr.length - 1) ? setX(0) : setX(x - 100);
    }
    return (
        <div className="slider">
            {sliderArr.map((item, ind) => {
                return (
                    <div key={ind} className="slide" style={{ transform: `translateX(${x}%)` }}>
                        {item}
                    </div>
                )
            })}
            <button
                className="slider__button slider__button--left"
                onClick={goLeft}>left</button>
            <button
                className="slider__button slider__button--right"
                onClick={goRight}
                ><i class="fa fa-chevron-right" aria-hidden="true"></i>
                </button>
        </div >
        // <div className="image-carousel">
        //     <div className="inner">
        //         <img src="http://localhost:8000/static/store_app/1.jpg" alt="" />
        //         <img src="http://localhost:8000/static/store_app/2.jpg" alt="" />
        //         <img src="http://localhost:8000/static/store_app/3.jpg" alt="" />
        //     </div>
        //     <div className="bubbles">
        //         <span className="bubble bubble__active"></span>
        //         <span className="bubble"></span>
        //         <span className="bubble"></span>
        //     </div>
        //     <div className="prev"></div>
        //     <div className="next"></div>
        // </div>
    )
}

export default IndexPage;
