import React, { useState, useEffect } from 'react';

import SliderImage from '../slider-img';

import './index-page.css';

const sliderArr = [1, 2, 3];

const IndexPage = () => {

    const [x, setX] = useState(0);

    const goRight = () => {
        setX(x => x === -100 * (sliderArr.length - 1) ? 0 : x - 100);
    }

    const goLeft = () => {
        setX(x => x === 0 ? -100 * (sliderArr.length - 1) : x + 100);
    }

    const [intervalId, setIntervalId] = useState(() => goRight);

    useEffect(() => {
        const id = setInterval(intervalId, 3000);
        return () => clearInterval(id);
    }, [intervalId])

    const handleLeftButtonClick = () => {
        goLeft();
        setIntervalId(() => goRight);
    }

    const handleRightButtonClickht = () => {
        goRight()
        setIntervalId(() => goRight)
    }

    return (
        <div className="slider" >
            {sliderArr.map(ind => {
                return (
                    <div key={ind} className="slide" style={{ transform: `translateX(${x}%)` }}>
                        {<SliderImage ind={ind} />}
                    </div>
                )
            })}
            <button
                className="slider__button slider__button--left"
                onClick={handleLeftButtonClick}>
                <i className="fa fa-chevron-left arrow" aria-hidden="true"></i>
            </button>
            <button
                className="slider__button slider__button--right"
                onClick={handleRightButtonClickht}>
                <i className="fa fa-chevron-right arrow" aria-hidden="true"></i>
            </button>
        </div >
        // <div className="image-carousel">
        //     <div className="inner">
        //         <img src="http://localhost:8000/static/store_app/1.jpg" alt="" />
        //         <img src="http://localhost:8000/static/store_app/2.jpg" alt="" />
        //         <img src="http://localhost:8000/static/store_app/3.jpg" alt="" />
        //     </div>
        //     <div className="bubbles">
        //         <span className="bubble bubble__active"></span>
        //         <span className="bubble"></span>
        //         <span className="bubble"></span>
        //     </div>
        //     <div className="prev"></div>
        //     <div className="next"></div>
        // </div>
    )
}

export default IndexPage;


