import React, { useState } from 'react';

import { connect } from 'react-redux';
import { setLoginModalWindow, login } from '../../redux/actions';

import './modal-login-window.css';

const ModalLoginWindow = ({ loginPopup, setLoginModalWindow, login, errors }) => {
    console.log(errors);

    const [loginData, setLoginData] = useState({ email: '', password: '' })

    const handleLoginInputChange = e => {
        setLoginData({
            ...loginData,
            [e.target.name]: e.target.value
        })
    }

    const handLoginFormSubmit = e => {
        e.preventDefault()
        login(loginData)
    }

    return loginPopup
        ? <div className="modal-login-layout">
            <div className="popup-login-window">
                <div
                    className="flag"
                    onClick={() => setLoginModalWindow(false)}>
                    <div className="popup-login-window__close-button">
                    </div>
                </div>
                <div className="popup-login-window__wrapper">
                    <p className="popup-login-window__title">АВТОРИЗАЦИЯ</p>
                    <form
                        className="login-form"
                        onSubmit={handLoginFormSubmit}>
                        <p className="login-form__errors">
                            {
                                errors.map(el, ind => <span>{el[0]}</span>)
                            }
                        </p>
                        <input
                            type="email"
                            name="email"
                            className="login-form__input"
                            placeholder="Email"
                            onChange={handleLoginInputChange} />
                        <input
                            type="password"
                            name="password"
                            className="login-form__input"
                            placeholder="Пароль"
                            onChange={handleLoginInputChange} />
                        <p className="login-form__paragraph">Забыли пароль?</p>
                        <button
                            type="submit"
                            className="login-form__button">
                            Войти</button>
                    </form>
                    <button className="login-form__button login-form__button--register">Регистрация</button>
                </div>
            </div>
        </div>
        : null

}

const mapStateToProps = state => ({
    loginPopup: state.modals.loginPopup,
    errors: state.auth.errors
});

export default connect(mapStateToProps, { setLoginModalWindow, login })(ModalLoginWindow);
